import React, { useContext, useRef } from 'react'
import './login.css'
import { Link } from "react-router-dom";
import { Context } from '../../context/Context';


export default function Login() {

  const userRef = useRef()
  const passRef = useRef()
  const { user, dispatch, isFetching} = useContext(Context)


  const handleSubmit = async (e)=>{
    e.preventDefault();
    dispatch({type:"LOGIN_START"})
    try {
      const res = await axios.post("/auth/login", {
        username: useRef.current.value,
        password: passRef.current.value
      })
      dispatch({type:"LOGIN_SUCCESS",payload: res.data})
      
    } catch (error) {
      dispatch({type:"LOGIN_FAIL"})

    }
  }
  console.log(user)

  return (
    <div className='login'>
        <span className="Title">Login</span>
        <form className="loginForm" onSubmit={handleSubmit}>
            <label >Username</label>
            <input type="text" ref={userRef} placeholder='Enter your username' />
            <label >Password</label>
            <input type="password" />
            <button className="loginButton" ref={passRef}type='submit'>Login</button>
        </form>
        <button className="loginRegisterButton">
        <Link to="/register" style={{textDecoration:"none", color:"inherit"}}>Register
        </Link>
        </button>
    
    </div>
  )
}
