import axios from 'axios'
import React, { useContext, useState } from 'react'
import './write.css'
import {Context} from "../../context/Context"
export default function Write() {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [file, setFile] = useState(null)

  const {user} = useContext(Context)

  const handleSubmit = (e)=>{
    e.preventDefault();
    const newPost = {
      title,
      desc
    }
    axios.post("/posts")

  }

  return (
    <div className='write'>
        <img className='writeImg' src="https://images.pexels.com/photos/1167355/pexels-photo-1167355.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" alt="" />
        <form className='writeForm' onSubmit={handleSubmit}>
            <div className="writeFormGroup">
                <label htmlFor="fileInput">
                <i className="writeIcon fa-solid fa-plus"></i>
                </label>
                <input type="file" id="fileInput" style={{display:"none"}} />
                <input className="writeInput"type="text" placeholder='Title' autoFocus={true} />
            </div>
            <div className="writeFormGroup">
            <textarea className="writeInput writeText"placeholder='Tell your story...' type="text"></textarea>
            </div>
            <button className='writeSubmit' type='submit'>Publish</button>

        
        </form>
    </div>
  )
}
